package com.hynet.heebit.components.widget.sticky;import com.hynet.heebit.components.widget.sticky.listener.OrientationProvider;import androidx.recyclerview.widget.LinearLayoutManager;import androidx.recyclerview.widget.RecyclerView;public class LinearLayoutOrientationProvider implements OrientationProvider {    @Override    public int getOrientation(RecyclerView recyclerView) {        RecyclerView.LayoutManager layoutManager = recyclerView.getLayoutManager();        throwIfNotLinearLayoutManager(layoutManager);        return ((LinearLayoutManager) layoutManager).getOrientation();    }    @Override    public boolean isReverseLayout(RecyclerView recyclerView) {        RecyclerView.LayoutManager layoutManager = recyclerView.getLayoutManager();        throwIfNotLinearLayoutManager(layoutManager);        return ((LinearLayoutManager) layoutManager).getReverseLayout();    }    private void throwIfNotLinearLayoutManager(RecyclerView.LayoutManager layoutManager) {        if (!(layoutManager instanceof LinearLayoutManager)) {            throw new IllegalStateException("StickyListHeadersDecoration can only be used with a " + "LinearLayoutManager.");        }    }}